datacheckitem.SQL:
SELECT fileName tblName FROM ARCSHAPE_METADATA WHERE SHPType<>0 and fileName<>'HD_DIVIDER_NODE_INDEX';
                   select '{tblName}' FileName, id RepeatId, count(*) RecoordCount from {tblName} 
                   group by id having count(*)>1;

select 'HD_DIVIDER_NODE' FileName, N.id RepeatId, count(*) RecoordCount from HD_DIVIDER_NODE N
                   join HD_DIVIDER_NODE_INDEX I on I.id = N.id
                  group by N.geometry,I.divider_id having count(*)>1;

SELECT FileName fileName, FileName||'_GEOCOORDS' tblName FROM ARCSHAPE_METADATA WHERE 
                   SHPType<>0;SELECT '{fileName}' FILENAME, FID, INDEXPOINT, X, Y, Z FROM {tblName} WHERE 
                   (X>360 OR X<0) OR (Y>90 OR Y<-90) OR (Z>8848 OR Z<-11034);

select id laneid, divider_l dividerid, 1 as isleft from hd_lane where divider_l not in 
                   (select id from hd_divider) union select id, divider_r, 0 from hd_lane where divider_r not in 
                   (select id from hd_divider);

SELECT FLANE_ID,TLANE_ID FROM HD_LANE_GEOCONN EXCEPT SELECT FLID, TLID FROM 
                   (SELECT FLANE_ID FLID,TLANE_ID TLID FROM HD_LANE_CONNECTIVITY UNION 
                   SELECT TLANE_ID,FLANE_ID FROM HD_LANE_CONNECTIVITY);

SELECT FLANE_ID,TLANE_ID FROM HD_LANE_CONNECTIVITY EXCEPT SELECT FLID, TLID FROM 
                   (SELECT FLANE_ID FLID, TLANE_ID TLID FROM HD_LANE_GEOCONN UNION 
                   SELECT TLANE_ID, FLANE_ID FROM HD_LANE_GEOCONN);

SELECT REPEATID, ID DIVIDERID FROM HD_DIVIDER D JOIN 
                   (SELECT DISTINCT geometry, MIN(ROWID) REPEATID FROM HD_DIVIDER 
                   GROUP BY geometry HAVING COUNT(*)>1) G ON D.GEOMETRY=G.GEOMETRY;

SELECT REPEATID, ID LANEID FROM HD_LANE D JOIN 
                   (SELECT DISTINCT geometry, MIN(ROWID) REPEATID FROM HD_LANE GROUP BY 
                   geometry HAVING COUNT(*)>1) G ON D.GEOMETRY=G.GEOMETRY AND REPEATID!=ID;
